<?if(!defined("B_PROLOG_INCLUDED") || B_PROLOG_INCLUDED!==true)die();?><tr>	<td colspan="2" style="font-size:18px;padding-top:20px;"><?echo GetMessage("XLS_PROFILE_HEADER") ?></td></tr><tr>	<td style="width:350px;vertical-align:top;"><?echo GetMessage("XLS_PROFILE_SELECT") ?></td>	<td>		<script type="text/javascript">			function xlsProfileSelectChange(select) {				if (select.value == '0') {					document.getElementById('xls_change_profile').disabled = true;					document.getElementById('xls_profile_name').disabled = true;					document.getElementById('xls_delete_profile').disabled = true;				}				else {					if (select.value == 'new') {						document.getElementById('xls_change_profile').disabled = true;						document.getElementById('xls_profile_name').disabled = false;						document.getElementById('xls_delete_profile').disabled = true;						document.getElementById('xls_profile_name').focus();					}					else {						document.getElementById('xls_change_profile').disabled = false;						document.getElementById('xls_profile_name').disabled = true;						document.getElementById('xls_delete_profile').disabled = false;					}				}			}						function xlsConfirmDeleteProfile() {				return window.confirm("Вы действительно хотите удалить профиль?");			}		</script>		<div style="float:left; padding-right:20px;">			<select name="XLS_PROFILE_SELECT" style="min-width:200px;" onchange="xlsProfileSelectChange(this)">				<option value="0">без профиля</option>				<?				$XlsDbProfiles = CXlsProfile::getAll();				while ($profile = $XlsDbProfiles->getNext()) {					?>					<option value="<?=$profile['id']?>" <?if ($XLS_PROFILE_SELECT == $profile['id']) echo 'selected'?>><?=$profile['name']?></option>					<?				}				?>				<option value="new">новый</option>			</select><br>			<input type="submit" name="XLS_CHANGE_PROFILE" id="xls_change_profile" value="Загрузить профиль" style="margin-top:10px; min-width:180px" <?if (!$XLS_PROFILE_SELECT || $XLS_PROFILE_SELECT == 'new') echo 'disabled'?>><br>			<input type="submit" name="XLS_DELETE_PROFILE" id="xls_delete_profile" value="Удалить профиль" style="margin-top:2px; min-width:180px" <?if (!$XLS_PROFILE_SELECT || $XLS_PROFILE_SELECT == 'new') echo 'disabled'?> onclick="return xlsConfirmDeleteProfile()">		</div>		<div>			<?echo GetMessage("XLS_PROFILE_COMMENT") ?>		</div>	</td></tr><tr>	<td style="width:350px;"><?echo GetMessage("XLS_PROFILE_NAME") ?></td>	<td>		<input type="text" name="XLS_PROFILE_NAME" id="xls_profile_name" style="min-width:200px;margin-top:10px;" <?if ($XLS_PROFILE_SELECT != 'new') echo 'disabled'?>>		</td></tr><tr>	<td colspan="2" style="font-size:18px;padding-top:20px;"><?echo GetMessage("XLS_LIST_HEADER") ?></td></tr><tr>	<td style="width:350px;"><?echo GetMessage("XLS_CHANGE_LIST") ?></td>	<td>		<select name="XLS_LIST" style="min-width:200px;">			<?			foreach ($XlsExcel->getAllSheets() as $index=>$Sheet) {								?>				<option value="<?=$index?>" <?if ($XLS_LIST == $index) echo 'selected'?>><?=$XlsExcel->iconv($Sheet->getTitle())?></option>				<?			}			?>		</select>	</td></tr><tr>	<td colspan="2" style="font-size:18px;padding-top:20px;"><?echo GetMessage("XLS_FIRST_HEADER") ?></td></tr><tr>	<td><?echo GetMessage("XLS_FIRST_ROW_HEADER") ?></td>	<td>		<input type="text" name="XLS_FIRST_ROW_HEADER" value="<?=htmlspecialchars($XLS_FIRST_ROW_HEADER)?>" autocomplete="off">	</td></tr><tr>	<td><?echo GetMessage("XLS_FIRST_COLUMN_HEADER") ?></td>	<td>		<input type="text" name="XLS_FIRST_COLUMN_HEADER" value="<?=htmlspecialchars($XLS_FIRST_COLUMN_HEADER)?>" autocomplete="off">	</td></tr><tr>	<td colspan="2" style="font-size:18px;padding-top:20px;"><?echo GetMessage("XLS_FIRST_DATA") ?></td></tr><tr>	<td><?echo GetMessage("XLS_FIRST_ROW") ?></td>	<td>		<input type="text" name="XLS_FIRST_ROW" value="<?=htmlspecialchars($XLS_FIRST_ROW)?>" autocomplete="off">	</td></tr><tr>	<td><?echo GetMessage("XLS_FIRST_COLUMN") ?></td>	<td>		<input type="text" name="XLS_FIRST_COLUMN" value="<?=htmlspecialchars($XLS_FIRST_COLUMN)?>" autocomplete="off">	</td></tr><?if ($XLS_IBLOCK_IS_CATALOG) {	?>	<tr>		<td colspan="2" style="font-size:18px;padding-top:20px;"><?echo GetMessage("XLS_PRICE_PARAMS") ?></td>	</tr>	<tr>		<td><?echo GetMessage("XLS_PRICE_PARAM_CURRENCY") ?></td>		<td>			<select name="XLS_PRICE_PARAM_CURRENCY">			<?			$dbCurrencies = CCurrency::GetList();			while ($arCurrency = $dbCurrencies->GetNext()) {				?>				<option value="<?=$arCurrency['CURRENCY']?>" <?if ($XLS_PRICE_PARAM_CURRENCY == $arCurrency['CURRENCY']) echo 'selected'?>><?=$arCurrency['FULL_NAME']?></option>				<?			}			?>			</select>		</td>	</tr>	<tr>		<td><?echo GetMessage("XLS_PRICE_PARAM_TYPE") ?></td>		<td>			<select name="XLS_PRICE_PARAM_TYPE">			<?			$dbCatalogGroups = CCatalogGroup::GetList();			while ($arCatalogGroup = $dbCatalogGroups->GetNext()) {				?>				<option value="<?=$arCatalogGroup['ID']?>" <?if ($XLS_PRICE_PARAM_TYPE == $arCatalogGroup['ID']) echo 'selected'?>><?=$arCatalogGroup['NAME_LANG']?></option>				<?			}			?>			</select>		</td>	</tr>	<tr>		<td colspan="2" style="font-size:18px;padding-top:20px;"><?echo GetMessage("XLS_BITRIX_PARAMS") ?></td>	</tr>	<tr>		<td><?echo GetMessage("XLS_BITRIX_COUNT_NULL") ?></td>		<td>			<input type="checkbox" name="XLS_BITRIX_COUNT_NULL" <?if ($XLS_BITRIX_COUNT_NULL == 1) echo ' checked '?> value="1">		</td>	</tr>	<tr>		<td><?echo GetMessage("XLS_BITRIX_PRICE_NULL") ?></td>		<td>			<input type="checkbox" name="XLS_BITRIX_PRICE_NULL" <?if ($XLS_BITRIX_PRICE_NULL == 1) echo ' checked '?> value="1">		</td>	</tr>	<?}