<?if(!is_object($GLOBALS["USER_FIELD_MANAGER"]))	return false;CModule::IncludeModule("softeffect.xls");IncludeModuleLangFile(__FILE__);$group_user_right = COption::GetOptionString("softeffect.xls", "softeffect_xls_group_users", "");if ($group_user_right!="") {	$arGroups = explode(",", $group_user_right);}$arUserGroups = $USER->GetUserGroupArray();$result = array_intersect($arGroups, $arUserGroups);if (count($result)>0 || $USER->IsAdmin()) {	$aMenu[] = array(		"parent_menu" => "global_menu_content",		"icon" => "iblock_menu_icon_settings",		//"section" => "xls_input",		"sort" => 300,		"text" =>GetMessage('SOFTEFFECT_XLS_LINK'),		"title"=>GetMessage('SOFTEFFECT_XLS_LINK'),		"url" => "xls_input_data_import.php?lang=".LANG,		//"module_id" => "xls_input",		//"items_id" => "xls_input",		"items_id" => "xls_input",		"more_url" => Array("xls_input_data_import.php"),	);	return $aMenu;}?>